import React from "react";
import StartPage from "./components/startPage";
import {BrowserRouter,Routes,Route} from "react-router-dom";
import Home from "./components/Home";
import LogIn from "./components/login";
import VerificationForm from "./components/verificationForm";
import RenewForm from "./components/renewForm";
import { useParams } from "react-router";
import { useNavigate } from "react-router";
import { useEffect } from "react";
import axios from "axios";

// import axios from "axios";
function App() {

    const navigator = useNavigate();

    const param = useParams();
    const [url,isvalidURL] = React.useState(false);

    useEffect( () =>{

        const verifyLink = async()=>{

            try{
                const url = `http://localhost:3000/${param.username}/verify`;
                const {username} = await axios.get(url);
                console.log("ol:",username); 
                isvalidURL(true);
                navigator('/login');
            }catch(err){
                console.log("err:",err);
                }

            }
        },param)




  return (




      <BrowserRouter>
           {/* <center><h1>WELCOME TO GOVERNMENT BUS SURVICES</h1></center> */}
            <Routes>
                <Route path="/" element={<StartPage />}></Route>
                <Route path='/Home' element={<Home />}>

                    <Route path="verify" element={<VerificationForm />}></Route>
                    <Route path="renew" element={<RenewForm />}></Route>
                </Route>

                <Route path='/login' element={<LogIn />}></Route>    
           </Routes>
      </BrowserRouter>



  )
}

export default App;
