import React, { useEffect } from "react";
import StartPage from "./components/startPage";
import {BrowserRouter,Routes,Route} from "react-router-dom";
import Home from "./components/Home";
import LogIn from "./components/login";
import VerificationForm from "./components/verificationForm";
import RenewForm from "./components/renewForm";
import { useParams,useNavigate } from 'react-router';
import axios from "axios";




// import axios from "axios";

function App() {

  const [validurl,seturl] = React.useState("");

  const {param} = useParams();
  const {data} = useParams();



  useEffect(()=> {

    const verifyAccount = async()=>{
    console.log("param:",param," ",data);
    const data1 = await axios.get(`/${param}/verify`);
    console.log("data:",data1);
    }

    verifyAccount()

},[param])


  return (



    <div>
      <BrowserRouter>
           {/* <center><h1>WELCOME TO GOVERNMENT BUS SURVICES</h1></center> */}
            <Routes>
                <Route path="/" element={<StartPage />}></Route>
                <Route path='/Home' element={<Home />}>

                    <Route path="verify" element={<VerificationForm />}></Route>
                    <Route path="renew" element={<RenewForm />}></Route>
                </Route>

                <Route path='/login' element={<LogIn />}></Route> 

                <Route path="/:data/verify" element={<LogIn />}></Route>
           </Routes>
      </BrowserRouter>

    </div>



  )
}

export default App;
