import React, { useEffect } from "react";
import StartPage from "./components/startPage";
import {BrowserRouter,Routes,Route} from "react-router-dom";
import Home from "./components/Home";
import LogIn from "./components/login";
import VerificationForm from "./components/verificationForm";
import RenewForm from "./components/renewForm";
import { useParams,useNavigate } from 'react-router';
import axios from "axios";



// import axios from "axios";
function App() {



  const [url,seturl] = React.useState(false);

  useEffect(()=> {
  verifyLink();

  async function verifyLink(){

         try{
          const param = useParams();
          console.log("pa:",param);
           const url1 = `http://localhost:3000/${param.username}/confirm`;
           console.log("url1:",url1);
           const data = await axios.get(url1);
           console.log("data:",data);
           seturl(true);
         }catch(err){
          console.log("err:",err);
         }
      }
},[param])


  return (



    <div>
      <BrowserRouter>
           {/* <center><h1>WELCOME TO GOVERNMENT BUS SURVICES</h1></center> */}
            <Routes>
                <Route path="/" element={<StartPage />}></Route>
                <Route path='/Home' element={<Home />}>

                    <Route path="verify" element={<VerificationForm />}></Route>
                    <Route path="renew" element={<RenewForm />}></Route>
                </Route>

                <Route path='/login' element={<LogIn />}></Route> 
           </Routes>
      </BrowserRouter>

    </div>



  )
}

export default App;
